import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { AppDispatch, RootState } from '../app/store';
import { fetchData } from '../features/example/exampleSlice'; // Import the action creator
import { FiHome } from 'react-icons/fi'; // Import the home icon

const Home: React.FC = () => {
  const dispatch = useDispatch<AppDispatch>();
  const { data, loading, error } = useSelector(
    (state: RootState) => state.example
  );

  const handleFetchData = () => {
    dispatch(fetchData()); // Dispatch the action creator
  };

  // Optional: Fetch data on initial load
  // useEffect(() => {
  //   dispatch(fetchData());
  // }, [dispatch]);

  return (
    <div className="container mx-auto p-4 flex flex-col items-center">
      <h1 className="text-2xl font-bold mb-4">
        <FiHome className="inline-block mr-2" /> Redux Saga Example
      </h1>

      <button
        onClick={handleFetchData}
        disabled={loading}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded disabled:opacity-50 disabled:cursor-not-allowed mb-4"
      >
        {loading ? 'Fetching...' : 'Fetch Data'}
      </button>

      <div className="w-full max-w-md p-4 border rounded shadow-md bg-white">
        <h2 className="text-xl mb-2">Data Fetch Status:</h2>
        {loading && (
          <div className="flex items-center text-yellow-600">
            <svg
              className="animate-spin -ml-1 mr-3 h-5 w-5 text-yellow-600"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
            >
              <circle
                className="opacity-25"
                cx="12"
                cy="12"
                r="10"
                stroke="currentColor"
                strokeWidth="4"
              ></circle>
              <path
                className="opacity-75"
                fill="currentColor"
                d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
              ></path>
            </svg>
            Loading data...
          </div>
        )}
        {error && (
          <div className="text-red-600 bg-red-100 p-2 rounded">
            <p className="font-semibold">Error fetching data:</p>
            <p>{error}</p>
          </div>
        )}
        {data && !loading && !error && (
          <div className="text-green-700 bg-green-100 p-2 rounded">
            <p className="font-semibold">Data fetched successfully:</p>
            <pre className="mt-2 text-sm overflow-x-auto">
              {JSON.stringify(data, null, 2)}
            </pre>
          </div>
        )}
        {!loading && !error && !data && (
           <p className="text-gray-500">Click the button to fetch data.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
